var promise = require('bluebird');

var options = {
  // Initialization Options
  promiseLib: promise
};

var pgp = require('pg-promise')(options);
var connectionString = "postgres://sharedserveruser:shared@localhost:5432/sharedserver";
var db = pgp(connectionString);



//GET: Returns all job positions.
function getAllJobPositions(req, res, next) {
	console.log("entro a getAllJobPositions");
  db.any('select name,category,description from job_positions')
    .then(function (data) {
      res.status(200)
        .json({
          job_positions: data,
		  metadata: {
					"version":0.1,
					"count": data.length					
					}
        });
    })
    .catch(function (err) {
		res.status(500).json({
		   "code":0,
		   "message":err.message
		});
    });
}


module.exports = {
  getAllJobPositions: getAllJobPositions
};

